import*as dom from"../dom.js";import{StandardKeyboardEvent}from"../keyboardEvent.js";import{StandardMouseEvent}from"../mouseEvent.js";import{Gesture}from"../touch.js";import{Disposable}from"../../common/lifecycle.js";export class Widget extends Disposable{onclick(e,o){this._register(dom.addDisposableListener(e,dom.EventType.CLICK,(e=>o(new StandardMouseEvent(e)))))}onmousedown(e,o){this._register(dom.addDisposableListener(e,dom.EventType.MOUSE_DOWN,(e=>o(new StandardMouseEvent(e)))))}onmouseover(e,o){this._register(dom.addDisposableListener(e,dom.EventType.MOUSE_OVER,(e=>o(new StandardMouseEvent(e)))))}onmouseleave(e,o){this._register(dom.addDisposableListener(e,dom.EventType.MOUSE_LEAVE,(e=>o(new StandardMouseEvent(e)))))}onkeydown(e,o){this._register(dom.addDisposableListener(e,dom.EventType.KEY_DOWN,(e=>o(new StandardKeyboardEvent(e)))))}onkeyup(e,o){this._register(dom.addDisposableListener(e,dom.EventType.KEY_UP,(e=>o(new StandardKeyboardEvent(e)))))}oninput(e,o){this._register(dom.addDisposableListener(e,dom.EventType.INPUT,o))}onblur(e,o){this._register(dom.addDisposableListener(e,dom.EventType.BLUR,o))}onfocus(e,o){this._register(dom.addDisposableListener(e,dom.EventType.FOCUS,o))}ignoreGesture(e){return Gesture.ignoreTarget(e)}}